INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(8786, 626, 'cooperation__text', 'Продукция «Новый день» имеет все необходимые заключения контролирующих органов и отличается высочайшим качеством, имеет индивидуальную упаковку, которая брендирована и красочно оформлена. Наличие в ассортименте магазина свежих овощей и салатов «Новый день» повышает посещаемость и поднимает продажи.'),
(8787, 626, '_cooperation__text', 'field_63fedb512f856'),
