(19641, 1132, '_cycle-swiper_2_sign-stage', 'field_642e2cfba445d'),
(19642, 1132, 'cycle-swiper_2_stage-name', 'Ежедневный уход и забота о растениях'),
(19643, 1132, '_cycle-swiper_2_stage-name', 'field_64003d37b66f5'),
